    public static ArrayList<Book> getListData() {
        ArrayList<Book> list = new ArrayList<>();
        for (String[] aData : data) {
            Book book = new Book();
            book.setName(aData[0]);
            book.setAuthor(aData[1]);
            book.setPublisher(aData[2]);
            book.setPage(aData[3]);
            book.setCategories(aData[4]);
            book.setDescription(aData[5]);
            book.setPhoto(aData[6]);

            list.add(book);
        }

        return list;
    }
